Scripts.OnEvent:
if (event == "COMBAT_LOG_EVENT_UNFILTERED") then
	RaidAlerter_CombatLogEvent(self, ...);
else
	RaidAlerter_OnEvent(self, event, ...);
end

Scripts.OnShow:
						getglobal(self:GetName().."Title"):SetText("|cFF20FF20"..RAL_TEXT_XML_34);
						self:SetValue(RaidAlerter_SET.MAXRAM);
						local MaxRamNum = self:GetValue();
						local MaxRamText = "";
						if (MaxRamNum >= 10240) then
							MaxRamText = format("%.1f", MaxRamNum/1024).."MB";
						elseif (MaxRamNum >= 1000) then
							MaxRamText = format("%.2f", MaxRamNum/1024).."MB";
						else
							MaxRamText = MaxRamNum.."KB";
						end
 						getglobal(self:GetName().."Value"):SetText("|cFF44FF44"..MaxRamText.."|r"..RAL_TEXT_XML_35);

Scripts.OnValueChanged:
						local MaxRamNum = self:GetValue();
						local MaxRamText = "";
						if (MaxRamNum >= 10240) then
							MaxRamText = format("%.1f", MaxRamNum/1024).."MB";
						elseif (MaxRamNum >= 1000) then
							MaxRamText = format("%.2f", MaxRamNum/1024).."MB";
						else
							MaxRamText = MaxRamNum.."KB";
						end
 						getglobal(self:GetName().."Value"):SetText("|cFF44FF44"..MaxRamText.."|r"..RAL_TEXT_XML_35);
						RaidAlerter_SET.MAXRAM = MaxRamNum;
